CREATE TABLE ADDRESS (IDADDRESS NUMBER(19) NOT NULL, ADDRESS VARCHAR NOT NULL, PROVINCE VARCHAR NOT NULL, REGION VARCHAR NOT NULL, TOWN VARCHAR NOT NULL, PRIMARY KEY (IDADDRESS))
CREATE TABLE BRAND (IDBRAND NUMBER(19) NOT NULL, INFO VARCHAR, NAME VARCHAR, PRIMARY KEY (IDBRAND))
CREATE TABLE DISCOUNT (IDDISCOUNT NUMBER(19) NOT NULL, DESCRIPTION VARCHAR, ENDING TIMESTAMP, FORMULA VARCHAR, NAME VARCHAR NOT NULL, STARTING TIMESTAMP, PRIMARY KEY (IDDISCOUNT))
CREATE TABLE EVALUATION (IDEVALUATION NUMBER(19) NOT NULL, NOTE VARCHAR, RATE NUMBER(10) NOT NULL, IDPRODUCT NUMBER(19), IDUSER NUMBER(19), PRIMARY KEY (IDEVALUATION))
CREATE TABLE LIST (IDLIST NUMBER(19) NOT NULL, DESCRIPTION VARCHAR, NAME VARCHAR NOT NULL, PRIMARY KEY (IDLIST))
CREATE TABLE PRODUCT (IDPRODUCT NUMBER(19) NOT NULL, DESCRIPTION VARCHAR, NAME VARCHAR NOT NULL, PRICE NUMBER(10,5), IDBRAND NUMBER(19), IDCATEGORY NUMBER(19), PRIMARY KEY (IDPRODUCT))
CREATE TABLE SHOP (IDSHOP NUMBER(19) NOT NULL, NAME VARCHAR, IDADDRESS NUMBER(19) UNIQUE, PRIMARY KEY (IDSHOP))
CREATE TABLE USER (IDUSER NUMBER(19) NOT NULL, NAME VARCHAR, PASSWORD VARCHAR NOT NULL, SURNAME VARCHAR, USERNAME VARCHAR NOT NULL, PRIMARY KEY (IDUSER))
CREATE TABLE CATEGORY (IDCATEGORY NUMBER(19) NOT NULL, DESCRIPTION VARCHAR, NAME VARCHAR, PRIMARY KEY (IDCATEGORY))
CREATE TABLE PRODUCTDISCOUNT (IDDISCOUNT NUMBER(19) NOT NULL, IDPRODUCT NUMBER(19) NOT NULL, PRIMARY KEY (IDDISCOUNT, IDPRODUCT))
CREATE TABLE PRODUCTLIST (IDLIST NUMBER(19) NOT NULL, IDPRODUCT NUMBER(19) NOT NULL, PRIMARY KEY (IDLIST, IDPRODUCT))
CREATE TABLE PRODUCTSHOP (IDSHOP NUMBER(19) NOT NULL, IDPRODUCT NUMBER(19) NOT NULL, PRIMARY KEY (IDSHOP, IDPRODUCT))
ALTER TABLE EVALUATION ADD CONSTRAINT FK_EVALUATION_IDUSER FOREIGN KEY (IDUSER) REFERENCES USER (IDUSER)
ALTER TABLE EVALUATION ADD CONSTRAINT FK_EVALUATION_IDPRODUCT FOREIGN KEY (IDPRODUCT) REFERENCES PRODUCT (IDPRODUCT)
ALTER TABLE PRODUCT ADD CONSTRAINT FK_PRODUCT_IDCATEGORY FOREIGN KEY (IDCATEGORY) REFERENCES CATEGORY (IDCATEGORY)
ALTER TABLE PRODUCT ADD CONSTRAINT FK_PRODUCT_IDBRAND FOREIGN KEY (IDBRAND) REFERENCES BRAND (IDBRAND)
ALTER TABLE SHOP ADD CONSTRAINT FK_SHOP_IDADDRESS FOREIGN KEY (IDADDRESS) REFERENCES ADDRESS (IDADDRESS)
ALTER TABLE PRODUCTDISCOUNT ADD CONSTRAINT FK_PRODUCTDISCOUNT_IDDISCOUNT FOREIGN KEY (IDDISCOUNT) REFERENCES DISCOUNT (IDDISCOUNT)
ALTER TABLE PRODUCTDISCOUNT ADD CONSTRAINT FK_PRODUCTDISCOUNT_IDPRODUCT FOREIGN KEY (IDPRODUCT) REFERENCES PRODUCT (IDPRODUCT)
ALTER TABLE PRODUCTLIST ADD CONSTRAINT FK_PRODUCTLIST_IDLIST FOREIGN KEY (IDLIST) REFERENCES LIST (IDLIST)
ALTER TABLE PRODUCTLIST ADD CONSTRAINT FK_PRODUCTLIST_IDPRODUCT FOREIGN KEY (IDPRODUCT) REFERENCES PRODUCT (IDPRODUCT)
ALTER TABLE PRODUCTSHOP ADD CONSTRAINT FK_PRODUCTSHOP_IDSHOP FOREIGN KEY (IDSHOP) REFERENCES SHOP (IDSHOP)
ALTER TABLE PRODUCTSHOP ADD CONSTRAINT FK_PRODUCTSHOP_IDPRODUCT FOREIGN KEY (IDPRODUCT) REFERENCES PRODUCT (IDPRODUCT)
